const PLUS_ONE = "PLUS_ONE"  // value는 상수로 생성
const MINUS_ONE = "MINUS_ONE"  // value는 상수로 생성

// 액션객체를 반환하는 함수 생성
// export가 붙는 이유는 plusOne()는 밖으로 나가서 사용될 예정이기 때문임
export const plusOne = () => {
    return {
        type: PLUS_ONE
    }
}

export const minusOne = () => {
    return {
        type: MINUS_ONE
    }
}

// 초기 상태값
const initialState = {
    number: 0,
}

// 리듀서
const counter = (state = initialState, action) => {
    console.log(action)
    switch (action.type) {
        // case에서도 문자열이 아닌, 위에서 선언한 상수를 넣어준다.
        case PLUS_ONE:
            return {
                // 기존 state에 있던 number에 +1을 더한다.
                number: state.number + 1
            }
        case MINUS_ONE:
            return {
                number: state.number - 1
            }
        default:
            return state
    }
}

// 모듈파일에는 리듀서를 export default 한다.
export default counter
