// Action Value
const SUBMIT_TODO = "SUBMIT_TODO"
const DELETE_TODO = "DELETE_TODO"
const EDIT_TODO = "EDIT_TODO"

// Action Creator
export const submitTodo = (payload) => {
    return {
        type: SUBMIT_TODO,
        payload,
    }
}
export const deleteTodo = (payload) => {
    return {
        type: DELETE_TODO,
        payload,
    }
}
export const editTodo = (payload) => {
    return {
        type: EDIT_TODO,
        payload,
    }
}

// Initial State
const InitialState = {
    todos: [
        {
            id: 1,
            title: "현재까지 완료한 내용",
            content: "미디어쿼리를 \n" + "이용한",
            isDone: true,
        },
        {
            id: 2,
            title: "4주차 너무 어렵다..ㅠ",
            content: "리덕스를 어려워하는 나는 똥멍청이",
            isDone: false,
        },
    ]
}

// Reducer
const todos = (state = InitialState, action) => {
    switch (action.type) {
        // 하고자 하는 액션 이름
        case SUBMIT_TODO:  // todo를 제출해서 todos에 합쳐줘!
            return {
                // 실행할 함수 결과
                // ...state,  // => 현재 initialState 안에 todos key 하나만 있어서 굳이 전개할 필요가 없음
                todos: [...state.todos, action.payload]
            }
        case DELETE_TODO:
            return {
                // ...state,
                todos: state.todos.filter((todo) => (todo.id !== action.payload))
            }
        case EDIT_TODO:
            return {
                // ...state,
                todos: state.todos.map((todo) => {
                    if (todo.id === action.payload) {
                        return {
                            ...todo, isDone: !todo.isDone
                        }
                    } else {
                        return todo
                    }
                })
            }
        default:
            return state
    }
}

// 모듈파일에는 리듀서를 export default 한다.
export default todos
