// 초기 상태값
const initialState = {
    number: 0,
}

// 리듀서
const counter = (state = initialState, action) => {
    console.log(action)
    switch (action.type) {
        // PLUS_ONE 이라는 case를 추가한다.
        // 여기서 말하는 case란, action의 type을 의미한다.
        // dispatch로부터 전달받은 action의 타입이 "PLUS_ONE"일 때
        // 아래 return 절이 실행된다.
        default:
            return state
    }
}

// 모듈파일에는 리듀서를 export default 한다.
export default counter
