import { useState } from "react"
import Layout from "../components/layout/Layout"
import Postbox from "../components/postbox/Postbox"
import Cards from "../components/cards/Cards"


function Guestbook() {
    const defaultPosts = [
        {
            id: 2,
            nickname: "쿠우형아",
            comment: "노래 너무 좋아요~!"
        },
        {
            id: 1,
            nickname: "쿠우",
            comment: "야오오옹 야오옹, 야..옹?!"
        },
    ]

    const localDB = JSON.parse(localStorage.getItem('posts'))
    console.log(localDB)

    let setTheDB = () => {
        (localDB.length === '') ? [...defaultPosts] : localDB
    }
    console.log(setTheDB)


    const [posts, setPosts] = useState(setTheDB)
    return (
        <Layout>
            <Postbox posts={posts} setPosts={setPosts} />
            <Cards posts={posts} setPosts={setPosts} />
        </Layout>
    )
}

export default Guestbook
