import './index.css';

function Post({ post, setPost, posts, setPosts }) {
    function onDeleteHandler(postID) {
        const newPosts = posts.filter((post) => {
            return post.id !== postID;
        })
        setPosts(newPosts)
    }
    return (
        <div className='card'>
            <div>
                <h3 className='card-comment'>{post.comment}</h3>
                <i className='card-nickname'>- {post.nickname}</i>
            </div>
            <button onClick={()=>onDeleteHandler} className='delete-btn'>삭제하기</button>
        </div>
    )
}

export default Post
