				/>
                <InputTitle>내용</InputTitle>
                <InputBody
				value={toDo.content}
				name="content"
				type="text"
				placeholder="Write your ToDo"
				onChange={onChangeHandler} />
            </InputArea>
            <InputBtn>추가하기</InputBtn>
        </FormCtn>
    )
}

export default Form
