// 초기 상태값
const initialState = {
    number: 0,
}

// 리듀서
const counter = (state = initialState, action) => {
    console.log(action)
    switch (action.type) {
        default:
            return state
    }
}

// 모듈파일에는 리듀서를 export default 한다.
export default counter
