import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import { submitTodo } from "../redux/modules/todos";
import styled from "styled-components"

const FormCtn = styled.form`
	background-color: #eee;
	display: flex;
	justify-content: space-between;
	align-items: center;
	padding: 20px;
	gap: 20px;
`

const InputArea = styled.div`
	display: flex;
	align-items: center;
	gap: 20px;
	font-weight: 700;
`
const InputTitle = styled.label`
	min-width: 30px;
`
const InputBody = styled.input`
	border: none;
	width: 250px;
	height: 40px;
	border-radius: 10px;
	padding: 0 12px;
`

const InputBtn = styled.button`
	background-color: #1a73e8;
	color: white;
	font-weight: 700;
	border: none;
	border-radius: 10px;
	height: 40px;
	width: 130px;
	&:hover {
		background-color: gray;
	}
`

function Form() {
	// form에 넣을 initialState(초기값) 생성
	const initialState = {
		id: 0,
		title: "",
		content: "",
		isDone: false,
	}

	// form 값을 수정할 수 있는 useState 생성
	const [todo, setTodo] = useState(initialState)

	// dispatch 함수 불러와!
	const dispatch = useDispatch()

	// 전역객체로 todos 불러와!
	const globalTodos = useSelector((state)=>state.todos.todos)

	// form input 값 바뀔 때, value에 넣어주고, todo에 업데이트!
	const onChangeHandler = (event) => {
		const { name, value } = event.target
		setTodo({...todo, [name]: value})
	}
	console.log(todo)

	const onSubmitHandler = (event) => {
		// form 안에 submit 역할을 하는 버튼을 눌렀어도 새로고침 못하게!
		event.preventDefault()

		// input 값에 공백 등 결과가 없을 때 바로 함수를 빠져나와라
		if (todo.title.trim() === "" || todo.content.trim() === "") {
			return;
		}

		dispatch(
			submitTodo({
				id: globalTodos[globalTodos.length-1].id+1,
				title: todo.title
			})
		)
	}
	console.log()
	return (
		<FormCtn onSubmit={onSubmitHandler}>
			<InputArea>
				<InputTitle>제목</InputTitle>
				<InputBody
					name="title"
					onChange={onChangeHandler}
				/>
				<InputTitle>내용</InputTitle>
				<InputBody
					name="content"
					onChange={onChangeHandler}
				/>
			</InputArea>
			<InputBtn>추가하기</InputBtn>
		</FormCtn>
	)
}

export default Form
