import './index.css';

function Post({ post, setPost, posts, setPosts }) {
    function onDeleteHandler(postID) {
        const newPosts = posts.filter((post) => {
            return post.id !== postID;
        })
        setPosts(newPosts)
    }
    return (
        <div className='card'>
                <div>
                    <h3 className='card-comment'>코멘트 들어갈 곳</h3>
                    <i className='card-nickname'>- 닉네임</i>
                </div>
                <button className='delete-btn'>삭제하기</button>
            </div>
    )
}

export default Post
