import { useState } from 'react';
import './index.css';

let count = 3;
function Postbox({ posts, setPosts }) {
    const [showing, setShowing] = useState(false)
    const showBox = () => {
        console.log(document.getElementsByClassName('mypost'))
        // if (showing === false) {
        //     document.getElementsByClassName('mypost').style.display='flex';
        // }else {
        //     document.getElementsByClassName('mypost').style.display='none'
        // }
        // setShowing(!showing);
    }
    const initialState = {
        id: 0,
        nickname: "",
        comment: "",
    }

    const [post, setPost] = useState(initialState)
    const onChangeHandler = (event) => {
        // const {name,value} = event.target;
        const name = event.target.name
        const value = event.target.value
        setPost({ ...post, [name]: value, id: count })
        console.log(post)
    }
    const onSubmitHandler = (event) => {
        event.preventDefault()
        if (post.nickname.trim() === "" || post.comment.trim() === "") {
            return;
        }
        setPosts(cur => [post, ...cur])
        setPost(initialState)
        count++
    }
    console.log(posts)

    return (
        <div style={{marginBottom: "20px"}}>
            <div className='wrap'>
                <div className='mypic'>
                    <h1 className='mypicTitle'>백예린 팬명록</h1>
                    <p className='mypicTemp'>현재 기온: <span id="temp">18.57</span>도</p>
                    <button onClick={showBox} className='posting-box'>POSTING BOX</button>
                </div>
            </div>
            <form className='mypost' onSubmit={onSubmitHandler}>
                <div className='form-floating'>
                    <label>닉네임</label>
                    <input
                        value={post.nickname}
                        className='form-control'
                        name="nickname"
                        placeholder='닉네임을 입력하세요.'
                        type="text"
                        onChange={onChangeHandler}
                    />
                </div>
                <div className='form-floating'>
                    <label>응원댓글</label>
                    <input
                        value={post.comment}
                        className='form-control'
                        name='comment'
                        placeholder='응원댓글을 남겨주세요.'
                        type="text"
                        onChange={onChangeHandler}
                    />
                </div>
                <button className='form-btn'>응원 남기기</button>
            </form>
        </div>
    )
}

export default Postbox
