import React from "react"
import { useSelector } from "react-redux";
import { useParams, useNavigate } from "react-router-dom"
import styled from "styled-components"

const DetailWrap = styled.div`
    margin: 0 auto;
    padding: 30px;
`
const DetailCtn = styled.div`
    max-width: 800px;
    min-width: 350px;
    width: 100%;
    box-sizing: border-box;
    margin: 30px auto;
    border: 3px ${props => props.borderStyle};
    border-radius: 10px;
    padding: 30px;
`
const DetailBackBox = styled.div`
    display: flex;
    justify-content: space-between;
`
const DetailState = styled.div`
    font-size: 14px;
    color: gray;
`
const DetailStateSet = styled.div`

`
const DetailBackBtn = styled.button`
    border: 2px solid #ddd;
    border-radius: 5px;
    background-color: transparent;
    padding: 5px 10px;
    color: gray;
    transition: all, 0.3s;
    &:hover {
        font-weight: 700;
        background-color: #ddd;
        color: white;
        cursor: pointer;
    }
`
const DetailHr = styled.hr`
    background-color: #ddd;
    border: none;
    margin: 30px 0;
    height: 1.5px;
`
const DetailBox = styled.div`
    border: 1px solid black;
    display: flex;
    flex-direction: column;
    gap: 20px;
`
const DetailTitle = styled.div`
    font-size: 30px;
    font-weight: 700;
`
const DetailContent = styled.div`
    font-size: 16px;
    color: gray;
`

const Detail = () => {
    const navigate = useNavigate()
    const { id } = useParams();
    console.log(id)

    // Ï†ÑÏó≠Í∞ùÏ≤¥Î°ú todos Î∂àÎü¨ÏôÄ!
    const globalTodos = useSelector((state) => state.todos.todos)
    console.log(globalTodos)
    const thisTodo = globalTodos.filter((todo) => todo.id === Number(id))[0]
    console.log(thisTodo)
    return (
        <DetailWrap>
            <DetailCtn
                borderStyle = {(thisTodo.isDone) ? "solid #1a73e8" : "dotted tomato"}
            >
                <DetailBackBox>
                    <DetailBackBtn onClick={() => navigate("/")}>
                        &#60; Ïù¥Ï†Ñ
                    </DetailBackBtn>
                    <DetailState>
                        ÏÉÅÌÉú : 
                    </DetailState>
                    <DetailStateSet>
                        {(thisTodo.isDone) ? "Done!!! üéâüéâüéâ" : "Working... üî•üî•üî•"}
                    </DetailStateSet>
                </DetailBackBox>
                <DetailHr/>
                <DetailBox>
                    <DetailTitle>
                        {thisTodo.title}
                    </DetailTitle>
                    <DetailContent>
                        {thisTodo.content}
                    </DetailContent>
                </DetailBox>
            </DetailCtn>
        </DetailWrap>
    )
}
export default Detail
