import React from "react"
import { useSelector } from "react-redux";
import { useParams, useNavigate } from "react-router-dom"
import styled from "styled-components"

const DetailWrap = styled.div`
    margin: 0 auto;
    padding: 30px;
`
const DetailCtn = styled.div`
    max-width: 800px;
    min-width: 350px;
    width: 95%;
    margin: 30px auto;
    border: 1px solid red;
    border-radius: 10px;
    padding: 30px;
`
const DetailBackBox = styled.div`

`
const DetailBackBtn = styled.button`
    border: 2px solid #ddd;
    border-radius: 5px;
    background-color: transparent;
    padding: 5px 10px;
    color: gray;
    transition: all, 0.3s;
    &:hover {
        font-weight: 700;
        background-color: #ddd;
        color: white;
        cursor: pointer;
    }
`
const DetailHr = styled.hr`
    background-color: #ddd;
    border: none;
    margin: 30px 0;
    height: 1.5px;
`
const DetailBox = styled.div`
    border: 1px solid black;
    display: flex;
    flex-direction: column;
    gap: 20px;
`
const DetailTitle = styled.div`
    font-size: 30px;
    font-weight: 700;
`
const DetailContent = styled.div`
    font-size: 16px;
`


const Detail = () => {
    const navigate = useNavigate()
    const { id } = useParams();
    console.log(id)

    // 전역객체로 todos 불러와!
    const globalTodos = useSelector((state) => state.todos.todos)
    console.log(globalTodos)
    const thisTodo = globalTodos.filter((todo) => todo.id === Number(id))[0]
    console.log(thisTodo)
    return (
        <DetailWrap>
            <DetailCtn>
                <DetailBackBox>
                    <DetailBackBtn onClick={() => navigate("/")}>
                        &#60; 이전
                    </DetailBackBtn>
                    <DetailHr/>
                </DetailBackBox>
                <DetailBox>
                    <DetailTitle>
                        {thisTodo.title}
                    </DetailTitle>
                    <DetailContent>
                        {thisTodo.content}
                    </DetailContent>
                </DetailBox>

                <h1>Title: "{thisTodo.title}"</h1>
                <p>Content: ""</p>
            </DetailCtn>
        </DetailWrap>
    )
}
export default Detail
