import { useDispatch, useSelector } from "react-redux"
import { resetTodo } from "../redux/modules/todos";
import styled from "styled-components"

const HeaderCtn = styled.div`
	color: #BBB;
	display: flex;
	justify-content: space-between;
	align-items: center;
	padding: 0 20px;
	margin: 0 10px;
	height: 50px;
	font-weight: 700;
`
const ResetBtn = styled.button`
	border: 2px solid black;
	border-radius: 50px;
	padding: 5px 10px;
	font-weight: 700;
	background-color: #ffcf36;
	transition: all, 0.3s;
	&:hover {
		background-color: tomato;
		color: white;
	}
`

function Header() {
	// dispatch 함수 불러와!
	const dispatch = useDispatch()
	// 전역객체로 todos 불러와!
	const globalTodos = useSelector((state) => state.todos.todos)

	const onResetHandler = () => {
		if (confirm('정말 리셋하시겠습니까?') === true) {
			alert("리셋되었습니다.")
			dispatch(resetTodo())
		} else {
			alert("신중히 생각하신 후, 리셋 버튼을 눌러주세요.")
		}	


	}
	return (
		<HeaderCtn>
			<div>My Todo List - by React.js</div>
			<ResetBtn onClick={()=>onResetHandler()}>
				RESET
			</ResetBtn>
		</HeaderCtn>
	)
}

export default Header
