import { useDispatch, useSelector } from "react-redux"
import { deleteTodo, editTodo } from "../redux/modules/todos";
import { Link } from "react-router-dom";
import styled from "styled-components"

const ListCtn = styled.div`
	padding: 20px;
    display: flex;
    justify-content: space-around;
`
const ListDiv = styled.div`
    display: flex;
    width: 50%;
    flex-direction: column;
    margin: 0 30px;
    max-width: 400px;
`

const ListTitle = styled.h2`
    text-align: center;
    color: ${props => props.color};
    margin: 0;
    padding-bottom: 20px;
    border-bottom: 2px solid #eee;
`
const ListBox = styled.div`
    margin-top: 30px;
    display: flex;
    flex-direction: column;
`

// todo ÏòÅÏó≠
const TodoCtn = styled.div`
    border: 3px solid ${props => props.color};
    border-radius: 10px;
    padding: 20px;
    margin-bottom: 20px;   
    transition: all, 0.3s;
    &:hover {
        transform: scale(1.05);
    }
`

const TodoHeader = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 10px;
`
const TodoTitle = styled.h2`
    margin: 0 0 15px 0;
`
const TodoDetailLink = styled.a`
    height: 30px;
    background-color: transparent;
    color: gray;
    border: none;
    transition: all, 0.3s;
    display: block;
    font-size: 14px;    
    &:hover {
        color: #1a73e8;
        font-weight: 700;
        cursor: pointer;
    }

`
const TodoContent = styled.div`
    color: gray;
    min-height: 70px;
`

const TodoBtnBox = styled.div`
    display: flex;
    justify-content: space-evenly;
    gap: 10px;
`
const TodoBtn = styled.button`
    font-weight: 700;
    min-width: 130px;
    width: 100%;
    height: 50px;
    border: 2px solid ${props => props.color};
    border-radius: 10px;
    transition: all, 0.3s;
    &:hover {
        background-color: ${props => props.color};
        color: white;
        cursor: pointer;
    };
`

function List() {
    // Ï†ÑÏó≠Í∞ùÏ≤¥Î°ú todos Î∂àÎü¨ÏôÄ!
    const globalTodos = useSelector((state) => state.todos.todos)

    // dispatch ÏûÑÌè¨Ìä∏
    const dispatch = useDispatch()

    // ÏÇ≠Ï†ú Î≤ÑÌäº Íµ¨ÌòÑ
    const onDeleteHandler = (todoID) => {
        dispatch(deleteTodo(todoID))
    }

    // Ï∑®ÏÜå/ÏôÑÎ£å Î≤ÑÌäº Íµ¨ÌòÑ
    const onEditHandler = (todoID) => {
        dispatch(editTodo(todoID))
    }
    console.log(globalTodos)
    return (
        <ListCtn>
            <ListDiv>
                <ListTitle color="#1a73e8">Working... üî•üî•üî•</ListTitle>
                <ListBox>
                    {
                        globalTodos.map((todo) => {
                            if (!todo.isDone) {
                                return (
                                    <TodoCtn
                                        color="#1a73e8"
                                        key={todo.id}
                                    >
                                        <TodoHeader>
                                            <TodoTitle>
                                                {todo.title}
                                            </TodoTitle>
                                            <TodoDetailLink>
                                                + ÏÉÅÏÑ∏Î≥¥Í∏∞
                                            </TodoDetailLink>
                                        </TodoHeader>
                                        <TodoContent>{
                                            todo.content.length > 125 ? `${todo.content.slice(0, 125)}...` : todo.content
                                        }
                                        </TodoContent>
                                        <TodoBtnBox>
                                            <TodoBtn
                                                color="red"
                                                onClick={() => onDeleteHandler(todo.id)}
                                            >
                                                ÏÇ≠Ï†úÌïòÍ∏∞
                                            </TodoBtn>
                                            <TodoBtn
                                                color="#1a73e8"
                                                onClick={() => onEditHandler(todo.id)}
                                            >
                                                {todo.isDone ? "Ï∑®ÏÜå" : "ÏôÑÎ£å"}
                                            </TodoBtn>
                                        </TodoBtnBox>
                                    </TodoCtn>
                                )
                            } else {
                                return null;
                            }
                        })
                    }
                </ListBox>
            </ListDiv>
            <ListDiv>
                <ListTitle color="tomato">Done!!! üéâüéâüéâ</ListTitle>
                <ListBox>
                    {
                        globalTodos.map((todo) => {
                            if (todo.isDone) {
                                return (
                                    <TodoCtn
                                        color="tomato"
                                        key={todo.id}
                                    >
                                        <TodoHeader>
                                            <TodoTitle>
                                                {todo.title}
                                            </TodoTitle>
                                            <TodoDetailLink >
                                                + ÏÉÅÏÑ∏Î≥¥Í∏∞
                                            </TodoDetailLink>
                                        </TodoHeader>
                                        <TodoContent>{
                                            todo.content.length > 125 ? `${todo.content.slice(0, 125)}...` : todo.content
                                        }
                                        </TodoContent>
                                        <TodoBtnBox>
                                            <TodoBtn
                                                color="red"
                                                onClick={() => onDeleteHandler(todo.id)}
                                            >
                                                ÏÇ≠Ï†úÌïòÍ∏∞
                                            </TodoBtn>
                                            <TodoBtn
                                                color="#1a73e8"
                                                onClick={() => onEditHandler(todo.id)}
                                            >
                                                {todo.isDone ? "Ï∑®ÏÜå" : "ÏôÑÎ£å"}
                                            </TodoBtn>
                                        </TodoBtnBox>
                                    </TodoCtn>
                                )
                            } else {
                                return null;
                            }
                        })
                    }
                </ListBox>
            </ListDiv>
        </ListCtn>
    )
}

export default List
